}

// Export types for easier importing
export type ActivityType = 
  | "page_view"
  | "contact_form_submission"
  | "successful_login"
  | "failed_login"
  | "calendly_booking"
  | "user_registration"
  | "password_reset"
  | "admin_action"
  | "system_event"
  | "security_alert";

export type ContactStatus = "new" | "read" | "responded";

export type SystemStatus = "healthy" | "warning" | "error";

export type DateRange = "today" | "yesterday" | "last7days" | "last30days" | "last90days" | "custom";

// Utility types
export type TimePeriod = "hour" | "day" | "week" | "month" | "year";

export interface DateRangeOption {
  label: string;
  value: DateRange;
  startDate: Date;
  endDate: Date;
}

// Form types
export interface AnalyticsFormData {
  dateRange: DateRange;
  startDate?: string;
  endDate?: string;
  metrics: string[];
}

export interface ExportOptions {
  format: "csv" | "xlsx" | "pdf";
  dateRange: DateRange;
  includeCharts: boolean;
  metrics: string[];
}

